export declare interface IDLElement {
  type: "interface" | "attribute" | "operation" | "callback";
}

export declare interface IDLType {
  idlType: any;
}

export declare interface IDLExternalAttribute {
  name: string;
}

export declare interface IDLAttribute extends IDLElement {
  name: string;
  idlType: IDLType;
  static: boolean;
  readonly: boolean;
}

export declare interface IDLOperation extends IDLElement {
  name: string;
  idlType: IDLType;
  static: boolean;
  arguments: IDLArgument [];
}

export declare interface IDLArgument extends IDLElement {
  name: string;
  idlType: IDLType;
}

export declare interface IDLInterface extends IDLElement {
  name: string;
  members: (IDLAttribute | IDLOperation) [];
  extAttrs: IDLExternalAttribute[];
}

export declare interface IDLCallback extends IDLElement {
  name: string;
  idlType: IDLType;
  arguments: IDLArgument [];
}

export declare function parse(s: string) : IDLElement[];
